import React from 'react'
import { useStore } from '../store';
import { listeyeEkle ,isaretle ,temizle} from '../store/liste/listeAction';


const Liste = () => {
    const { listeState, dispatchListe } = useStore();
    const { liste } = listeState;
    return (
      <div>
        <h1>YAPILACAKLAR LİSTESİ</h1>
        <input onChange={(e) => dispatchListe(listeyeEkle(e.target.value))} />
        <button onClick={(e) => dispatchListe(listeyeEkle(e.target.value))}>
          Ekle
        </button>
        <div>
          {liste.map((item) => {
            <div className={item.tamamlandi ? "yapildi":""}
            key={item.id} onClick={() => dispatchListe(isaretle(item.id))}>
              {item.baslik}
            </div>;
          })}
        </div>

<button onClick={()=>dispatchListe(temizle())}> Temizle</button>

      </div>
    );
  };
  export default Liste;
