import { types } from "../types";
import { listeInitialState } from "./listeInitialState";
export const listeReducer = (state = listeInitialState, action) => {
  if (action.type === types.EKLE) {
    return {
      ...state,
      liste: [
        ...state.liste,
        {
          id: state.liste.length + 1,
          baslik: action.payload,
          tamamlandi: false,
        },
      ],
    };
  }
