  if (action.type === types.ISARETLE) {
    return {
      ...state,
      liste: state.liste.map((item) =>
        item.id === action.payload
          ? { ...item, tamamlandi: !item.tamamlandi }
          : item
      ),
    };
  }
  if (action.type === types.TEMIZLE) {
    return {
      ...state,
      liste: state.liste.filter((item) => item.tamamlandi === false),
    };
  }
};
