import React from "react";
import students from "../assets/data/student.json";

const Tablo2 = () => {

const still1={
color:"crimson",
fontWeight:"bolder",
backgroundColor:"aquamarine" ,

};


const still2={
    color:"yellow",
    fontWeight:"bolder",
    backgroundColor:"gray" ,
    
    };





  return (
    <div>
     
      <h1 >KURSIYERLER</h1>
      <table>
        <thead>
          <tr style={still}>
            <th>ISIM</th>
            <th>YAS</th>
            <th>KURSU</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student, index) => {                                               
            return (
              <tr style={index %2==0?still1:still2}   key={index}>
                <td>{index}-{student.isim}</td>
                <td>{student.yas}</td>
                <td>{student.kurs}</td>
              </tr>
            );
          })}
        </tbody>
      
      </table>
    </div>
  );
};

export default Tablo2;
