import React from "react";
 

const Kisi = (prop) => {
  const { isim, img, yas, kurs } = prop;
const denemeStyle = {
  margin:"0.001rem",
  color:"blue"
  

};
  

  return (
    
      <div >
      <h1 style={{...denemeStyle , color:"red" }}>{isim}</h1>
      <img style={{...denemeStyle ,  borderRadius:"9rem" }} src={img} alt="" width={100} height={100} />
      <h4  style={denemeStyle} >{yas}</h4>
      <h4  style={denemeStyle} >{kurs}</h4>
      
    </div>
    
  );
};

export default Kisi;
