import React from "react";

const Kisi = (prop) => {
  const { isim, img, yas, kurs } = prop;
  const denemeStyle = {
    margin: "0.001rem",
    color: "blue",
    
    "&:hover": {
      borderradius:"0rem"
    },
   
  };
  // borderRadius:"9rem" ,:hover:{width="200" ,height="200"}

  return (
    <div>
      <h1 style={{ ...denemeStyle, color: "blue" }}>{isim}</h1>
      <img
        style={{ ...denemeStyle, borderRadius: "9rem" }}
        src={img}
        alt=""
        width={100}
        height={100}
      />
      <h4 style={{ ...denemeStyle, color: "green" }}>{yas}</h4>
      <h4 style={{ ...denemeStyle, color: "red" }}>{kurs}</h4>
    </div>
  );
};

export default Kisi;
